INSERT INTO `sep_posts` VALUES (312,1,'2014-01-25 05:15:43','2014-01-25 05:15:43','<p style=\"text-align: center;\"><a title=\"InnerVision Homepage\" href=\"http://www.kpfk.org/programs/118-innervisiontuesdays.html\" target=\"_blank\"><img style=\"vertical-align: middle;\" alt=\"KPFK Logo\" src=\"http://www.kpfk.org/templates/ja_purity/images/logo.png\" /></a></p>\r\n<p style=\"text-align: justify;\"></p>\r\n<p style=\"text-align: justify;\">On this episode (which also features <a title=\"Dr. Bonnie Goldstein\'s homepage \" href=\"http://www.drbonniegoldstein.com/\" target=\"_blank\">Bonnie Goldstein, PhD</a> of Lifespan Learning and <a title=\"Irving Yalom information \" href=\"http://www.yalom.com/\" target=\"_blank\">Irving Yalom, M.D.</a>) of Inner Vision, Dr. Levine discussed his participation in this <a href=\"http://somaticexperiencing.com/export/2013/01/29/ucla-presents-how-people-change-relationships-and-neuroplasticity-in-psychotherapy/\">weekend\'s attachment conference at UCLA</a>, as well as the origins of his interest in trauma.</p>\r\n<p style=\"text-align: justify;\"></p>\r\n<p style=\"text-align: justify;\">You can <a title=\"MP3 Download\" href=\"http://archive.kpfk.org/mp3/kpfk_130305_130030ivtue.MP3\" target=\"_blank\">download</a> or <a title=\"Streaming Playlist version\" href=\"http://archive.kpfk.org/m3u.php?mp3fil=13852\" target=\"_blank\">stream (M3u File)</a> the interview here (links will be deactivated by Early June)</p>\r\n<p style=\"text-align: center;\">(<a title=\"Future proofing the mp3 download link\" href=\"images/stories/audio/KPFK_Inner_Vision_Tuesdays%20w_Nita.mp3\" target=\"_blank\">Download Permalink</a>)</p>','Peter Levine on Inner Vision with Dr. Nita Vallens','','inherit','open','open','','217-revision-v1','','','2014-01-25 05:15:43','2014-01-25 05:15:43','',217,'http://localhost:8888/speed/217-revision-v1/',0,'revision','',0);
