INSERT INTO `sep_posts` VALUES (411,1,'2014-05-22 02:31:59','2014-05-22 02:31:59','<h1 style=\"text-align: center;\">Resources For Treatment and SE</h1> <h1 style=\"text-align: center;\">The Foundation for Human Enrichment Website:</h1> <h3>Important links for the heart of SE: The Foundation for Human Enrichment</h3> <h4 style=\"padding-left: 60px;\"><a href=\"http://www.traumahealing.com\" target=\"_blank\" title=\"The Foundation for Human Enrichment\">The Foundation for Human Enrichment</a></h4> <blockquote> <blockquote> <ul> <li>This website will help you find a therapist, find info on trainings in your area, read research on SE, as well as link you to other SE communities across the globe.</li> </ul> </blockquote> </blockquote> <h4 style=\"padding-left: 60px;\"><a href=\"http://www.traumahealing.com/somatic-experiencing/practitioner-directory.html\" target=\"_blank\" title=\"FHE Practitioner Registry\">Get help</a></h4> <blockquote> <blockquote> <ul> <li> <p>Peter no longer does individual sessions, but he encourages you to head to the Foundation for Human Enrichment, and find someone certified in Somatic Experiencing in your area.</p> </li> </ul> </blockquote> </blockquote> <p> </p> <h2>Trauma and Somatic Researchers:</h2> <h3>The following links are from important neuroscientists or authors in the field of trauma. They are excellent resources for anyone looking to learn more about trauma.</h3> <h3>Bessel Van Der Kolk and Trauma Center</h3> <h3><a href=\"http://www.traumacenter.org \" target=\"_blank\">http://www.traumacenter.org</a></h3> <h3>Stephen Porges</h3> <h3><a href=\"http://www.stephenporges.info \" target=\"_blank\">http://www.stephenporges.info</a></h3> <h3>Dan Siegel</h3> <h3><a href=\"http://drdansiegel.com \" target=\"_blank\">http://drdansiegel.com </a></h3> <h3>Antonio Damasio</h3> <h3><a href=\" http://scholar.google.com/scholar?hl=en&amp;q=damasio-a&amp;btnG=Search \" target=\"_blank\">http://scholar.google.com/scholar?hl=en&amp;q=damasio-a&amp;btnG=Search </a></h3> <h3>David Baldwin</h3> <h3><a href=\" http://www.trauma-pages.com\" target=\"_blank\">http://www.trauma-pages.com</a></h3> <ul> <li>  <ul> </ul> </li> <li>  <ul> </ul> </li> <li>  <ul> </ul> </li> <li>  <ul> </ul> </li> </ul>','Links','','inherit','open','open','','95-revision-v1','','','2014-05-22 02:31:59','2014-05-22 02:31:59','',95,'http://localhost:8888/speed/95-revision-v1/',0,'revision','',0);
